mapper.select:
SELECT USER_ID, USER_NAME, USER_NICKNAME, USER_EMAIL, USER_MOBILE, USER_BIRTH, USER_ADDRESS, USER_GENDER, USER_ACTIVATE, USER_AUTH, USER_HIREDATE FROM GAJAE.USERS WHERE
USER_NAME =#{USER_NAME}
AND USER_EMAIL = #{USER_EMAIL}

mapper.update:
UPDATE GAJAE.USERS
SET

	USER_NAME = #{USER_NAME}


	USER_NICKNAME = #{USER_NICKNAME}


	USER_EMAIL = #{USER_EMAIL}


	USER_MOBILE = #{USER_MOBILE}


	USER_BIRTH = #{USER_BIRTH}


	USER_ADDRESS = #{USER_ADDRESS}


	USER_GENDER = #{USER_GENDER}

, USER_EDIT_DATE = 	DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s')
WHERE
USER_ID = #{USER_ID}

mapper.select:
SELECT
USER_ID
FROM GAJAE.USERS
WHERE USER_NAME =#{USER_NAME}
AND USER_EMAIL =
#{USER_EMAIL}

SELECT
USER_PW
FROM GAJAE.USERS
WHERE USER_ID =#{USER_ID}
AND USER_MOBILE =
#{USER_MOBILE}

mapper.update:
UPDATE GAJAE.USERS
SET USER_PHOTO = #{USER_PHOTO}
WHERE
USER_ID = #{USER_ID}

mapper.select:
SELECT USER_ACTIVATE
FROM GAJAE.USERS
WHERE USER_ID=#{USER_ID}
AND USER_PW=#{USER_PW}
