mapper.select:
SELECT
COUNT(*)
FROM GAJAE.WISHLIST
WHERE USER_ID = #{USER_ID}
AND P_ID = #{P_ID}

SELECT
WL_ACTIVATE
FROM GAJAE.WISHLIST
WHERE USER_ID = #{USER_ID}
AND P_ID = #{P_ID}

mapper.update:
UPDATE GAJAE.WISHLIST
SET
WL_ACTIVATE = 'Y'
WHERE USER_ID=#{USER_ID}
AND P_ID= #{P_ID}

UPDATE GAJAE.WISHLIST
SET
WL_ACTIVATE = 'N'
WHERE USER_ID=#{USER_ID}
AND P_ID= #{P_ID}
